INSERT INTO `ii_add_article` VALUES ('116', '<p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space: pre;\">			</span>{volist name=&quot;cfields&quot; id=&quot;vo&quot;}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">			</span>&lt;tr&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">				</span>&lt;td&gt;{$vo.title}:&lt;/td&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">				</span>&lt;td&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">					</span>{switch name=&quot;vo.type&quot;}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">						</span>{case value=&quot;textarea&quot;}&lt;textarea name=&quot;{$key}&quot; id=&quot;{$key}&quot; rows=&quot;6&quot;&gt;&lt;/textarea&gt;{/case}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">						</span>{case value=&quot;input&quot;}&lt;input type=&quot;text&quot; name=&quot;{$key}&quot; id=&quot;{$key}&quot; value=&quot;&quot;&gt;{/case}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">						</span>{case value=&quot;file&quot;}&lt;input type=&quot;file&quot; name=&quot;{$key}&quot; id=&quot;{$key}&quot;&gt;{/case}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">						</span>{case value=&quot;select&quot;}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">							</span>&lt;select name=&quot;{$key}&quot; id=&quot;{$key}&quot;&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">				</span> &nbsp; &nbsp; &nbsp;<span style=\"white-space:pre\">	</span>{volist name=&quot;vo.select&quot; id=&quot;vo&quot;}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">				</span> &nbsp; &nbsp; &nbsp; &nbsp;&lt;option value=&quot;{$key}&quot;&gt;{$vo}&lt;/option&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">				</span> &nbsp; &nbsp; &nbsp; &nbsp;{/volist}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">				</span> &nbsp; &nbsp; &nbsp;&lt;/select&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">			</span> &nbsp; &nbsp; &nbsp;{/case}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">			</span> &nbsp; &nbsp; &nbsp;{case value=&quot;html&quot;}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">							</span>&lt;script type=&quot;text/javascript&quot; charset=&quot;utf-8&quot; src=&quot;/ueditor/ueditor.config.js&quot;&gt;&lt;/script&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">							</span>&lt;script type=&quot;text/javascript&quot; charset=&quot;utf-8&quot; src=&quot;/ueditor/ueditor.all.min.js&quot;&gt;&lt;/script&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">							</span>&lt;script id=&quot;editor&quot; name=&quot;{$key}&quot; type=&quot;text/plain&quot; style=&quot;width:100%;height:500px;&quot;&gt;&lt;/script&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">							</span>&lt;script type=&quot;text/javascript&quot;&gt;var ue=UE.getEditor(&#39;editor&#39;);&lt;/script&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">			</span> &nbsp; &nbsp; &nbsp;{/case}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">						</span>{default /}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">					</span>{/switch}</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">				</span>&lt;/td&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space:pre\">			</span>&lt;/tr&gt;</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"white-space: pre;\">		</span> &nbsp;{/volist}</p><p><br/></p>', '0');
INSERT INTO `ii_add_article` VALUES ('117', '<p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">Amaze UI 定义的一些基础样式。</p><h2 style=\"box-sizing: border-box; margin: 2em 0px 1.6rem; font-size: 1.25em; counter-increment: header2 1; counter-reset: header3 0; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">CSS 盒模型<a href=\"http://amazeui.org/css/base#css-he-mo-xing\" title=\"Heading anchor\" style=\"box-sizing: border-box; background-color: transparent; text-decoration-line: none; color: rgb(204, 204, 204); float: right;\"></a></h2><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">曾几何时，IE 6 及更低版本的<span style=\"box-sizing: border-box; font-weight: 700;\"><em style=\"box-sizing: border-box;\">非标准</em></span>盒模型被喷得体无完肤。IE 原来的盒模型真的不好么？最终，时间给了另外一个答案。 W3C 终认识到所谓标准盒模型在实际使用中的复杂性，于是在 CSS3 中增加&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">box-sizing</code>&nbsp;这一属性，允许用户自定义盒模型。</p><blockquote style=\"box-sizing: border-box; margin: 1.6rem 0px; padding-top: 5px; padding-bottom: 5px; padding-left: 15px; border-left: 4px solid rgb(221, 221, 221); font-family: Georgia, &quot;Times New Roman&quot;, Times, Kai, &quot;Kaiti SC&quot;, KaiTi, BiauKai, FontAwesome, serif; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\"><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1.6rem; text-indent: 2em; text-align: left;\"><span style=\"box-sizing: border-box; font-weight: 700;\">You tell me I&#39;m wrong, Then you better prove you&#39;re right.</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 0px; text-indent: 2em; text-align: left;\"><small>King of Pop – Scream</small></p></blockquote><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">这就是 W3C 的证明。</p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">扯远了，Amaze UI 将所有元素的盒模型设置为&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">border-box</code>。这下好了，妈妈再也不用担心你没计算好&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">padding</code>、<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">border</code>&nbsp;而使布局破相了。咱来写样式，不是来学算术。</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"box-sizing: border-box; display: inline-block; padding: 3px 8px; border-bottom-left-radius: 2px; border-top-right-radius: 2px;\">&nbsp;Copy</span></p><pre class=\"\" style=\"box-sizing: border-box; white-space: pre-wrap; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; margin-top: 1rem; margin-bottom: 1rem; padding: 1rem; line-height: 1.6; word-break: break-all; word-wrap: break-word; color: rgb(85, 85, 85); border: 1px solid rgb(222, 222, 222); border-radius: 0px;\">&nbsp;*,\r\n&nbsp;*:before,\r\n&nbsp;*:after&nbsp;{&nbsp;&nbsp;&nbsp;-moz-box-sizing:&nbsp;border-box;&nbsp;&nbsp;&nbsp;-webkit-box-sizing:&nbsp;border-box;&nbsp;&nbsp;&nbsp;box-sizing:&nbsp;border-box;\r\n&nbsp;}</pre><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\"><br/></p><p style=\"text-align: center;\"><img src=\"/upload/image/20170608/1496916366289302.png\" alt=\"Box sizing\"/></p><p><br/></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">参考链接：</p><ul style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; padding-left: 2em; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\" class=\" list-paddingleft-2\"><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"https://developer.mozilla.org/en-US/docs/Web/CSS/box-sizing\" target=\"_blank\" style=\"box-sizing: border-box; background-color: transparent; text-decoration-line: none; color: rgb(14, 144, 210);\">https://developer.mozilla.org/en-US/docs/Web/CSS/box-sizing</a></p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://www.paulirish.com/2012/box-sizing-border-box-ftw/\" target=\"_blank\" style=\"box-sizing: border-box; background-color: transparent; text-decoration-line: none; color: rgb(14, 144, 210);\">http://www.paulirish.com/2012/box-sizing-border-box-ftw/</a></p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://css-tricks.com/box-sizing/\" target=\"_blank\" style=\"box-sizing: border-box; background-color: transparent; text-decoration-line: none; color: rgb(14, 144, 210);\">Box Sizing</a></p></li></ul><h2 style=\"box-sizing: border-box; margin: 2em 0px 1.6rem; font-size: 1.25em; counter-increment: header2 1; counter-reset: header3 0; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">字号及单位<a href=\"http://amazeui.org/css/base#zi-hao-ji-dan-wei\" title=\"Heading anchor\" style=\"box-sizing: border-box; background-color: transparent; text-decoration-line: none; color: rgb(204, 204, 204); float: right;\"></a></h2><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">Amaze UI 将浏览器的基准字号设置为&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">62.5%</code>，也就是&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">10px</code>，现在&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">1rem = 10px</code>&nbsp;—— 为了计算方便。然后在&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">body</code>&nbsp;上应用了&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">font-size: 1.6rem;</code>，将页面字号设置为&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">16px</code>。</p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"box-sizing: border-box; display: inline-block; padding: 3px 8px; border-bottom-left-radius: 2px; border-top-right-radius: 2px;\">&nbsp;Copy</span></p><pre class=\"\" style=\"box-sizing: border-box; white-space: pre-wrap; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; margin-top: 1rem; margin-bottom: 1rem; padding: 1rem; line-height: 1.6; word-break: break-all; word-wrap: break-word; color: rgb(85, 85, 85); border: 1px solid rgb(222, 222, 222); border-radius: 0px;\">html&nbsp;{&nbsp;&nbsp;font-size:&nbsp;62.5%;\r\n}body&nbsp;{&nbsp;&nbsp;font-size:&nbsp;1.6rem;&nbsp;/*&nbsp;=16px&nbsp;*/}</pre><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">与&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">em</code>&nbsp;根据上下文变化不同，<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">rem</code>&nbsp;只跟基准设置关联，只要修改基准字号，所有使用&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">rem</code>&nbsp;作为单位的设置都会相应改变。</p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">当然，并非所有所有浏览器的默认字号都是&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">16px</code>，所以在不同的浏览器上会有细微差异。</p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">另外，一些需要根据字号做相应变化的场景也使用了&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">em</code>，需要像素级别精确的场景也使用了&nbsp;<code style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, FontAwesome, monospace; font-size: 1.3rem; padding: 2px 4px; color: rgb(199, 37, 78); background-color: rgb(248, 248, 248); white-space: nowrap; border-radius: 0px;\">px</code>。</p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\"><span style=\"box-sizing: border-box; font-weight: 700;\">参考资源：</span></p><ul style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; padding-left: 2em; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\" class=\" list-paddingleft-2\"><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://snook.ca/archives/html_and_css/font-size-with-rem\" target=\"_blank\" style=\"box-sizing: border-box; background-color: transparent; text-decoration-line: none; color: rgb(14, 144, 210);\">FONT SIZING WITH REM</a></p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://blog.typekit.com/2011/11/09/type-study-sizing-the-legible-letter/\" target=\"_blank\" style=\"box-sizing: border-box; background-color: transparent; text-decoration-line: none; color: rgb(14, 144, 210);\">Type study: Sizing the legible letter</a></p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"https://offroadcode.com/prototypes/rem-calculator/\" target=\"_blank\" style=\"box-sizing: border-box; background-color: transparent; text-decoration-line: none; color: rgb(14, 144, 210);\">The rem checker</a></p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://css-tricks.com/snippets/css/less-mixin-for-rem-font-sizing/\" target=\"_blank\" style=\"box-sizing: border-box; background-color: transparent; text-decoration-line: none; color: rgb(14, 144, 210);\">Mixins for Rem Font Sizing</a></p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://css-tricks.com/snippets/css/less-mixin-for-rem-font-sizing/\" target=\"_blank\" style=\"box-sizing: border-box; background-color: transparent; text-decoration-line: none; color: rgb(14, 144, 210);\"></a>234342345325345</p></li></ul>', '0');
INSERT INTO `ii_add_article` VALUES ('0', '<h2 style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px; color: rgb(63, 63, 63); font-family: 微软雅黑; text-indent: 2em; text-align: left;\">实例</h2><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">输出数组中的当前元素的值：</p><pre style=\"margin-top: 15px; margin-bottom: 0px; padding: 20px; border: 1px dotted rgb(119, 136, 85); font-family: Consolas; width: 765px; background-color: rgb(245, 245, 245); line-height: 1.4;\">&lt;?php\r\n$people&nbsp;=&nbsp;array(&quot;Bill&quot;,&nbsp;&quot;Steve&quot;,&nbsp;&quot;Mark&quot;,&nbsp;&quot;David&quot;);\r\n\r\necho&nbsp;current($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;\r\n?&gt;</pre><p style=\"margin-top: 15px; margin-bottom: 0px; padding: 8px 18px; border: none; line-height: 21px; display: inline-block; outline: 0px; vertical-align: middle; overflow: hidden; color: rgb(255, 255, 255); background-color: rgb(233, 104, 107); text-align: left; transition: 0.2s ease-out; cursor: pointer; white-space: nowrap; box-shadow: rgba(0, 0, 0, 0.12) 0px 2px 6px, rgba(0, 0, 0, 0.24) 0px 1px 2px; font-family: 微软雅黑; border-radius: 2px; text-indent: 2em;\"><a target=\"_blank\" href=\"http://www.w3school.com.cn/tiy/s.asp?f=demo_php_func_current\" style=\"margin: 0px; padding: 0px; border: 0px; text-decoration-line: none; color: rgb(255, 255, 255); background: transparent;\">运行实例</a></p><h2 style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px; color: rgb(63, 63, 63); font-family: 微软雅黑; text-indent: 2em; text-align: left;\">定义和用法</h2><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">current() 函数返回数组中的当前元素的值。</p><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">每个数组中都有一个内部的指针指向它的&quot;当前&quot;元素，初始指向插入到数组中的第一个元素。</p><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-weight: bold; color: rgb(255, 153, 85);\">提示：</span>该函数不会移动数组内部指针。要做到这一点，请使用&nbsp;<a href=\"http://www.w3school.com.cn/php/func_array_krsort.asp\" title=\"PHP krsort() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">next()</a>&nbsp;和&nbsp;<a href=\"http://www.w3school.com.cn/php/func_array_krsort.asp\" title=\"PHP krsort() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">prev()</a>&nbsp;函数。</p><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">相关的方法：</p><ul style=\"margin-top: 10px; margin-left: 35px; padding: 0px; border: 0px;\" class=\" list-paddingleft-2\"><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/func_array_end.asp\" title=\"PHP end() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">end()</a>&nbsp;- 将内部指针指向数组中的最后一个元素，并输出</p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/func_array_next.asp\" title=\"PHP next() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">next()</a>&nbsp;- 将内部指针指向数组中的下一个元素，并输出</p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/func_array_prev.asp\" title=\"PHP prev() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">prev()</a>&nbsp;- 将内部指针指向数组中的上一个元素，并输出</p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/func_array_reset.asp\" title=\"PHP reset() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">reset()</a>&nbsp;- 将内部指针指向数组中的第一个元素，并输出</p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/func_array_each.asp\" title=\"PHP each() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">each()</a>&nbsp;- 返回当前元素的键名和键值，并将内部指针向前移动</p></li></ul><h2 style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px; color: rgb(63, 63, 63); font-family: 微软雅黑; text-indent: 2em; text-align: left;\">语法</h2><pre style=\"margin-top: 15px; margin-bottom: 0px; padding: 20px; border: 1px dotted rgb(119, 136, 85); font-family: Consolas; width: 765px; background-color: rgb(245, 245, 245); line-height: 1.4;\">current(array)</pre><table width=\"809\"><tbody style=\"margin: 0px; padding: 0px; border: 0px;\"><tr style=\"margin: 0px; padding: 0px; border: 0px; background-color: rgb(245, 245, 245);\" class=\"firstRow\"><th style=\"margin: 0px; padding-right: 15px; padding-left: 6px; border-color: rgb(63, 63, 63); vertical-align: baseline; background-color: rgb(63, 63, 63); text-align: left; color: rgb(255, 255, 255); width: 139px;\">参数</th><th style=\"margin: 0px; padding-right: 15px; padding-left: 6px; border-color: rgb(63, 63, 63); vertical-align: baseline; background-color: rgb(63, 63, 63); text-align: left; color: rgb(255, 255, 255);\">描述</th></tr><tr style=\"margin: 0px; padding: 0px; border: 0px; background-color: rgb(255, 255, 255);\"><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\"><em style=\"margin: 0px; padding: 0px; border: 0px;\">array</em></td><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\">必需。规定要使用的数组。</td></tr></tbody></table><h3 style=\"margin: 25px 0px 0px; padding: 0px; border: 0px; font-size: 16px; text-indent: 2em; text-align: left;\">说明</h3><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">current() 函数返回数组中的当前元素（单元）。</p><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">每个数组中都有一个内部的指针指向它“当前的”元素，初始指向插入到数组中的第一个元素。</p><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">current() 函数返回当前被内部指针指向的数组元素的值，并不移动指针。如果内部指针指向超出了单元列表的末端，current() 返回 FALSE。</p><h2 style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px; color: rgb(63, 63, 63); font-family: 微软雅黑; text-indent: 2em; text-align: left;\">技术细节</h2><table width=\"809\"><tbody style=\"margin: 0px; padding: 0px; border: 0px;\"><tr style=\"margin: 0px; padding: 0px; border: 0px; background-color: rgb(245, 245, 245);\" class=\"firstRow\"><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\" width=\"139\">返回值：</td><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\">返回数组中的当前元素的值，如果当前元素为空或者当前元素没有值则返回 FALSE。</td></tr><tr style=\"margin: 0px; padding: 0px; border: 0px; background-color: rgb(255, 255, 255);\"><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\">PHP 版本：</td><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\">4+</td></tr></tbody></table><h2 style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px; color: rgb(63, 63, 63); font-family: 微软雅黑; text-indent: 2em; text-align: left;\">更多实例</h2><h3 style=\"margin: 25px 0px 0px; padding: 0px; border: 0px; font-size: 16px; text-indent: 2em; text-align: left;\">例子 1</h3><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">演示所有相关的方法：</p><pre style=\"margin-top: 15px; margin-bottom: 0px; padding: 20px; border: 1px dotted rgb(119, 136, 85); font-family: Consolas; width: 765px; background-color: rgb(245, 245, 245); line-height: 1.4;\">&lt;?php\r\n$people&nbsp;=&nbsp;array(&quot;Bill&quot;,&nbsp;&quot;Steve&quot;,&nbsp;&quot;Mark&quot;,&nbsp;&quot;David&quot;);\r\n\r\necho&nbsp;current($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;当前元素是&nbsp;Billecho&nbsp;next($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;Bill&nbsp;的下一个元素是&nbsp;Steveecho&nbsp;current($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;现在当前元素是&nbsp;Steveecho&nbsp;prev($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;Steve&nbsp;的上一个元素是&nbsp;Billecho&nbsp;end($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;最后一个元素是&nbsp;Davidecho&nbsp;prev($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;David&nbsp;之前的元素是&nbsp;Markecho&nbsp;current($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;目前的当前元素是&nbsp;Markecho&nbsp;reset($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;把内部指针移动到数组的首个元素，即&nbsp;Billecho&nbsp;next($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;Bill&nbsp;的下一个元素是&nbsp;Steveprint_r&nbsp;(each($people));&nbsp;//&nbsp;返回当前元素的键名和键值（目前是&nbsp;Steve），并向前移动内部指针?&gt;</pre><p style=\"margin-top: 15px; margin-bottom: 0px; padding: 8px 18px; border: none; line-height: 21px; display: inline-block; outline: 0px; vertical-align: middle; overflow: hidden; color: rgb(255, 255, 255); background-color: rgb(233, 104, 107); text-align: left; transition: 0.2s ease-out; cursor: pointer; white-space: nowrap; box-shadow: rgba(0, 0, 0, 0.12) 0px 2px 6px, rgba(0, 0, 0, 0.24) 0px 1px 2px; font-family: 微软雅黑; border-radius: 2px; text-indent: 2em;\"><a target=\"_blank\" href=\"http://www.w3school.com.cn/tiy/s.asp?f=demo_php_internal_pointer\" style=\"margin: 0px; padding: 0px; border: 0px; text-decoration-line: none; color: rgb(255, 255, 255); background: transparent;\">运行实例</a></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/php_ref_array.asp\" title=\"PHP Array 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">PHP Array 函数</a></p><p><br/></p>', '0');
INSERT INTO `ii_add_article` VALUES ('125', '<h2 style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px; color: rgb(63, 63, 63); font-family: 微软雅黑; text-indent: 2em; text-align: left;\">实例</h2><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">输出数组中的当前元素的值：</p><pre style=\"margin-top: 15px; margin-bottom: 0px; padding: 20px; border: 1px dotted rgb(119, 136, 85); font-family: Consolas; width: 765px; background-color: rgb(245, 245, 245); line-height: 1.4;\">&lt;?php\r\n$people&nbsp;=&nbsp;array(&quot;Bill&quot;,&nbsp;&quot;Steve&quot;,&nbsp;&quot;Mark&quot;,&nbsp;&quot;David&quot;);\r\n\r\necho&nbsp;current($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;\r\n?&gt;</pre><p style=\"margin-top: 15px; margin-bottom: 0px; padding: 8px 18px; border: none; line-height: 21px; display: inline-block; outline: 0px; vertical-align: middle; overflow: hidden; color: rgb(255, 255, 255); background-color: rgb(233, 104, 107); text-align: left; transition: 0.2s ease-out; cursor: pointer; white-space: nowrap; box-shadow: rgba(0, 0, 0, 0.12) 0px 2px 6px, rgba(0, 0, 0, 0.24) 0px 1px 2px; font-family: 微软雅黑; border-radius: 2px; text-indent: 2em;\"><a target=\"_blank\" href=\"http://www.w3school.com.cn/tiy/s.asp?f=demo_php_func_current\" style=\"margin: 0px; padding: 0px; border: 0px; text-decoration-line: none; color: rgb(255, 255, 255); background: transparent;\">运行实例</a></p><h2 style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px; color: rgb(63, 63, 63); font-family: 微软雅黑; text-indent: 2em; text-align: left;\">定义和用法</h2><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">current() 函数返回数组中的当前元素的值。</p><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">每个数组中都有一个内部的指针指向它的&quot;当前&quot;元素，初始指向插入到数组中的第一个元素。</p><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-weight: bold; color: rgb(255, 153, 85);\">提示：</span>该函数不会移动数组内部指针。要做到这一点，请使用&nbsp;<a href=\"http://www.w3school.com.cn/php/func_array_krsort.asp\" title=\"PHP krsort() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">next()</a>&nbsp;和&nbsp;<a href=\"http://www.w3school.com.cn/php/func_array_krsort.asp\" title=\"PHP krsort() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">prev()</a>&nbsp;函数。</p><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">相关的方法：</p><ul style=\"margin-top: 10px; margin-left: 35px; padding: 0px; border: 0px;\" class=\" list-paddingleft-2\"><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/func_array_end.asp\" title=\"PHP end() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">end()</a>&nbsp;- 将内部指针指向数组中的最后一个元素，并输出</p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/func_array_next.asp\" title=\"PHP next() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">next()</a>&nbsp;- 将内部指针指向数组中的下一个元素，并输出</p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/func_array_prev.asp\" title=\"PHP prev() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">prev()</a>&nbsp;- 将内部指针指向数组中的上一个元素，并输出</p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/func_array_reset.asp\" title=\"PHP reset() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">reset()</a>&nbsp;- 将内部指针指向数组中的第一个元素，并输出</p></li><li><p style=\"text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/func_array_each.asp\" title=\"PHP each() 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">each()</a>&nbsp;- 返回当前元素的键名和键值，并将内部指针向前移动</p></li></ul><h2 style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px; color: rgb(63, 63, 63); font-family: 微软雅黑; text-indent: 2em; text-align: left;\">语法</h2><pre style=\"margin-top: 15px; margin-bottom: 0px; padding: 20px; border: 1px dotted rgb(119, 136, 85); font-family: Consolas; width: 765px; background-color: rgb(245, 245, 245); line-height: 1.4;\">current(array)</pre><table width=\"809\"><tbody style=\"margin: 0px; padding: 0px; border: 0px;\"><tr style=\"margin: 0px; padding: 0px; border: 0px; background-color: rgb(245, 245, 245);\" class=\"firstRow\"><th style=\"margin: 0px; padding-right: 15px; padding-left: 6px; border-color: rgb(63, 63, 63); vertical-align: baseline; background-color: rgb(63, 63, 63); text-align: left; color: rgb(255, 255, 255); width: 139px;\">参数</th><th style=\"margin: 0px; padding-right: 15px; padding-left: 6px; border-color: rgb(63, 63, 63); vertical-align: baseline; background-color: rgb(63, 63, 63); text-align: left; color: rgb(255, 255, 255);\">描述</th></tr><tr style=\"margin: 0px; padding: 0px; border: 0px; background-color: rgb(255, 255, 255);\"><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\"><em style=\"margin: 0px; padding: 0px; border: 0px;\">array</em></td><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\">必需。规定要使用的数组。</td></tr></tbody></table><h3 style=\"margin: 25px 0px 0px; padding: 0px; border: 0px; font-size: 16px; text-indent: 2em; text-align: left;\">说明</h3><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">current() 函数返回数组中的当前元素（单元）。</p><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">每个数组中都有一个内部的指针指向它“当前的”元素，初始指向插入到数组中的第一个元素。</p><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">current() 函数返回当前被内部指针指向的数组元素的值，并不移动指针。如果内部指针指向超出了单元列表的末端，current() 返回 FALSE。</p><h2 style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px; color: rgb(63, 63, 63); font-family: 微软雅黑; text-indent: 2em; text-align: left;\">技术细节</h2><table width=\"809\"><tbody style=\"margin: 0px; padding: 0px; border: 0px;\"><tr style=\"margin: 0px; padding: 0px; border: 0px; background-color: rgb(245, 245, 245);\" class=\"firstRow\"><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\" width=\"139\">返回值：</td><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\">返回数组中的当前元素的值，如果当前元素为空或者当前元素没有值则返回 FALSE。</td></tr><tr style=\"margin: 0px; padding: 0px; border: 0px; background-color: rgb(255, 255, 255);\"><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\">PHP 版本：</td><td style=\"margin: 0px; padding: 6px 15px 6px 6px; border-color: rgb(170, 170, 170); vertical-align: text-top;\">4+</td></tr></tbody></table><h2 style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px; color: rgb(63, 63, 63); font-family: 微软雅黑; text-indent: 2em; text-align: left;\">更多实例</h2><h3 style=\"margin: 25px 0px 0px; padding: 0px; border: 0px; font-size: 16px; text-indent: 2em; text-align: left;\">例子 1</h3><p style=\"margin-top: 12px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\">演示所有相关的方法：</p><pre style=\"margin-top: 15px; margin-bottom: 0px; padding: 20px; border: 1px dotted rgb(119, 136, 85); font-family: Consolas; width: 765px; background-color: rgb(245, 245, 245); line-height: 1.4;\">&lt;?php\r\n$people&nbsp;=&nbsp;array(&quot;Bill&quot;,&nbsp;&quot;Steve&quot;,&nbsp;&quot;Mark&quot;,&nbsp;&quot;David&quot;);\r\n\r\necho&nbsp;current($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;当前元素是&nbsp;Billecho&nbsp;next($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;Bill&nbsp;的下一个元素是&nbsp;Steveecho&nbsp;current($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;现在当前元素是&nbsp;Steveecho&nbsp;prev($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;Steve&nbsp;的上一个元素是&nbsp;Billecho&nbsp;end($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;最后一个元素是&nbsp;Davidecho&nbsp;prev($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;David&nbsp;之前的元素是&nbsp;Markecho&nbsp;current($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;目前的当前元素是&nbsp;Markecho&nbsp;reset($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;把内部指针移动到数组的首个元素，即&nbsp;Billecho&nbsp;next($people)&nbsp;.&nbsp;&quot;&lt;br&gt;&quot;;&nbsp;//&nbsp;Bill&nbsp;的下一个元素是&nbsp;Steveprint_r&nbsp;(each($people));&nbsp;//&nbsp;返回当前元素的键名和键值（目前是&nbsp;Steve），并向前移动内部指针?&gt;</pre><p style=\"margin-top: 15px; margin-bottom: 0px; padding: 8px 18px; border: none; line-height: 21px; display: inline-block; outline: 0px; vertical-align: middle; overflow: hidden; color: rgb(255, 255, 255); background-color: rgb(233, 104, 107); text-align: left; transition: 0.2s ease-out; cursor: pointer; white-space: nowrap; box-shadow: rgba(0, 0, 0, 0.12) 0px 2px 6px, rgba(0, 0, 0, 0.24) 0px 1px 2px; font-family: 微软雅黑; border-radius: 2px; text-indent: 2em;\"><a target=\"_blank\" href=\"http://www.w3school.com.cn/tiy/s.asp?f=demo_php_internal_pointer\" style=\"margin: 0px; padding: 0px; border: 0px; text-decoration-line: none; color: rgb(255, 255, 255); background: transparent;\">运行实例</a></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; border: 0px; line-height: 21px; text-indent: 2em; text-align: left;\"><a href=\"http://www.w3school.com.cn/php/php_ref_array.asp\" title=\"PHP Array 函数\" style=\"margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: solid; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(144, 11, 9); border-left-color: initial; border-image: initial; text-decoration-line: none; color: rgb(144, 11, 9); background: transparent;\">PHP Array 函数</a>3</p><p><br/></p>', '0');
